import React, { useState } from 'react';
import './App.css';

const initialPeople = [
  { id: 'K', name: 'K' },
  { id: 'A', name: 'A' },
  { id: 'H', name: 'H' },
];

function App() {
  const [people, setPeople] = useState(initialPeople);
  const [iPaid, setIPaid] = useState([]);
  const [theyPaid, setTheyPaid] = useState([]);

  // Simulate swipe left/right with buttons for simplicity
  const handleArchive = (id, type) => {
    const person = people.find(p => p.id === id);
    setPeople(people.filter(p => p.id !== id));
    if (type === 'iPaid') setIPaid([...iPaid, person]);
    else setTheyPaid([...theyPaid, person]);
  };

  return (
    <div className="container">
      <h1>Lunchapp</h1>
      <div className="list">
        {people.length === 0 && <div className="empty">No more people</div>}
        {people.map(person => (
          <div className="card" key={person.id}>
            <span>{person.name}</span>
            <div className="actions">
              <button className="left" onClick={() => handleArchive(person.id, 'iPaid')}>← I paid</button>
              <button className="right" onClick={() => handleArchive(person.id, 'theyPaid')}>They paid →</button>
            </div>
          </div>
        ))}
      </div>
      <div className="archive">
        <h2>I paid</h2>
        {iPaid.length === 0 ? <div className="empty">None</div> : iPaid.map(p => <div key={p.id}>{p.name}</div>)}
        <h2>They paid</h2>
        {theyPaid.length === 0 ? <div className="empty">None</div> : theyPaid.map(p => <div key={p.id}>{p.name}</div>)}
      </div>
    </div>
  );
}

export default App;
